<?php$JSON = json_decode(file_get_contents('php://input'),true); //catch POST requestsinclude_once "database.php";//convert to datetime type$JSON['published_at'] = str_replace("T", " ", $JSON['published_at']);$JSON['published_at'] = substr($JSON['published_at'],0,-5);$result_event = mysqli_query($db, "SELECT * FROM events WHERE published_at >= date_sub(now(), INTERVAL 1 MINUTE) AND handled = 1");$event_exists = false;for ($i = 0; $i < mysqli_num_rows($result_event); ++$i) {    $row_event = mysqli_fetch_array($result_event);    if ($row_event['data'] == $JSON['data'] && $row_event['coreid'] == $JSON['coreid']) {        $event_exists = true;    }}if (!$event_exists) {    $result = mysqli_query($db, "INSERT INTO events(data, published_at, coreid) VALUES('$JSON[data]', '$JSON[published_at]', '$JSON[coreid]')");}//log errors$file = '/storage/ssd2/534/8325534/public_html/logFile.txt';$fOpen = fopen($file,'a'); //Открываем файл или создаём если его нет$text = date("d.m.y H:i:s") . PHP_EOL;if ($result == 'TRUE') {    $text .= "Success";}else {    $text .= mysqli_error($db);}$text .= PHP_EOL;fwrite($fOpen, $text); //Записываемfclose($fOpen); //Закрываем файл?>